package Operators;

public class Arthmetic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =10;
		
		int b = 4;
		
		System.out.println(a+b);// 14
		
		System.out.println(a-b);//6
		
		System.out.println(a*b);//40
		
		System.out.println(a/b);//2
		
		System.out.println(a%b);//2
		

	}

}
